update production.product   SET name = substring((upper(reverse(name) )  || ? ) , ? , ? ) ::varchar , listprice = ? ::numeric , reorderpoint = (reorderpoint + ?::int2 ) ::int2   WHERE (productid = ? )   returning "productid", "name", "productnumber", "makeflag", "finishedgoodsflag", "color", "safetystocklevel", "reorderpoint", "standardcost", "listprice", "size", "sizeunitmeasurecode", "weightunitmeasurecode", "weight", "daystomanufacture", "productline", "class", "style", "productsubcategoryid", "productmodelid", "sellstartdate"::text, "sellenddate"::text, "discontinueddate"::text, "rowguid", "modifieddate"::text 